// Chatbot.jsx

import React, { useState } from "react";
import SearchBar from "../shared/SearchBar";
import "../styles/chatbot.css";

const Chatbot = () => {
  const [generatedContent, setGeneratedContent] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleContentUpdate = async (content) => {
    setIsLoading(true); // Set loading state to true

    // Check if content exists and has parts array
    if (content && content.parts && content.parts.length > 0) {
      // Access the text section of the content
      const text = preprocessContent(content.parts[0].text);
      // Update the state with the text
      setGeneratedContent(text);
    } else {
      // If content is empty or does not have parts array
      setGeneratedContent("No content found");
    }

    setIsLoading(false); // Set loading state to false after processing
  };

  // Function to preprocess the content
  const preprocessContent = (text) => {
    // Split the text by '**' to identify bold sections
    const boldSections = text.split("**");

    // Process each section
    const processedText = boldSections.map((section, index) => {
      // Apply bold formatting to odd-indexed sections (i.e., enclosed within '**')
      if (index % 2 === 1) {
        return <strong key={index}>{section}</strong>;
      } else {
        // Add line breaks after every full stop
        const lines = section.split(". ");
        return lines.map((line, idx) => (
          <React.Fragment key={idx}>
            {line}.<br />
          </React.Fragment>
        ));
      }
    });

    // Concatenate processed sections and return
    return processedText;
  };

  return (
    <div className="chatbot">
      <h1>Gemini API Content Generator</h1>
      <SearchBar onContentUpdate={handleContentUpdate} />
      <div className="generated-content">
        <h2>Generated Content:</h2>
        {/* Show loader if content is loading */}
        {isLoading ? (
          <div>Loading...</div>
        ) : (
          // Render processed content
          generatedContent
        )}
      </div>
    </div>
  );
};

export default Chatbot;
