import React, { useState, useEffect } from "react";
import axios from "axios";

const Hotels = () => {
  const [hotels, setHotels] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedHotel, setSelectedHotel] = useState(null);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const response = await axios.get(
          "https://booking-com15.p.rapidapi.com/api/v1/hotels/search",
          {
            params: {
              text: searchQuery,
            },
            headers: {
              "Content-Type": "application/json",
              "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
              "X-RapidAPI-Key":
                "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
            },
          }
        );
        setHotels(response.data.hotels);
      } catch (error) {
        console.error("Error fetching hotels:", error);
      }
    };

    if (searchQuery.trim() !== "") {
      fetchHotels();
    }
  }, [searchQuery]);

  const handleSearch = () => {
    // Function to handle search button click
    if (searchQuery.trim() !== "") {
      // Check if search query is not empty
      fetchHotels(); // Call fetchHotels function
    }
  };

  const handleHotelSelect = async (hotelId) => {
    // Function to handle hotel selection
    try {
      const hotelResponse = await axios.get(
        `https://booking-com15.p.rapidapi.com/api/v1/hotels/${hotelId}`,
        {
          headers: {
            "Content-Type": "application/json",
            "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
            "X-RapidAPI-Key":
              "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
          },
        }
      );
      setSelectedHotel(hotelResponse.data.hotel);

      const reviewsResponse = await axios.get(
        `https://booking-com15.p.rapidapi.com/api/v1/hotels/${hotelId}/reviews`,
        {
          headers: {
            "Content-Type": "application/json",
            "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
            "X-RapidAPI-Key":
              "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
          },
        }
      );
      setReviews(reviewsResponse.data.reviews);
    } catch (error) {
      console.error("Error fetching hotel details:", error);
    }
  };

  return (
    <div>
      <h1>Hotel Search</h1>
      <div className="search-container">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Enter location or hotel name"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <div className="hotels-container">
        {hotels.map((hotel) => (
          <div key={hotel.hotel_id} className="hotel-card">
            <h2>{hotel.hotel_name}</h2>
            <p>Address: {hotel.hotel_address}</p>
            <button onClick={() => handleHotelSelect(hotel.hotel_id)}>
              View Details
            </button>
          </div>
        ))}
      </div>
      {selectedHotel && (
        <div className="hotel-details">
          <h2>{selectedHotel.hotel_name}</h2>
          <p>Address: {selectedHotel.hotel_address}</p>
          <h3>Reviews:</h3>
          <ul className="review-list">
            {reviews.map((review) => (
              <li key={review.review_id} className="review-item">
                {review.comment}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Hotels;
