// Hotels.jsx
import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";

const Hotels = () => {
  const history = useHistory();
  const [showHotels, setShowHotels] = useState(false);
  const [location, setLocation] = useState("");
  const [hotels, setHotels] = useState([]);

  const handleShowHotels = async () => {
    if (!location) {
      alert("Please enter a location");
      return;
    }
    setShowHotels(true);
    try {
      const response = await axios.get(
        "https://booking-com15.p.rapidapi.com/api/v1/hotels/search",
        {
          params: {
            text: location,
          },
          headers: {
            "Content-Type": "application/json",
            "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
            "X-RapidAPI-Key":
              "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
          },
        }
      );
      setHotels(response.data.hotels);
      // Assuming you have a route named "/hotels" for displaying hotels
      history.push("/hotels");
    } catch (error) {
      console.error("Error fetching hotels:", error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      handleShowHotels();
    }
  };

  const handleLocationChange = (e) => {
    setLocation(e.target.value);
  };

  return (
    <div>
      <h1>Hotel Search</h1>
      <input
        type="text"
        value={location}
        onChange={handleLocationChange}
        onKeyPress={handleKeyPress} // Add onKeyPress event handler
        placeholder="Enter location"
      />
      <button onClick={handleShowHotels}>Show Hotels</button>
      {showHotels && (
        <div>
          <h2>Hotels in {location}</h2>
          <div className="hotels-container">
            {hotels.map((hotel) => (
              <div key={hotel.hotel_id} className="hotel-card">
                <h3>{hotel.hotel_name}</h3>
                <p>Address: {hotel.hotel_address}</p>
                {/* Add more details as needed */}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Hotels;
