import React, { useState, useEffect } from "react";
import axios from "axios";

const Hotels = () => {
  const [hotels, setHotels] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    const fetchHotels = async () => {
      try {
        const response = await axios.get(
          "https://booking-com15.p.rapidapi.com/api/v1/hotels/searchDestination",
          {
            params: {
              query: searchQuery,
              // Add any additional parameters for filtering, sorting, etc.
            },
            headers: {
              "Content-Type": "application/json",
              "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
              "X-RapidAPI-Key":
                "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
            },
          }
        );
        setHotels(response.data); // Assuming the API returns an array of hotels
      } catch (error) {
        console.error("Error fetching hotels:", error);
      }
    };

    if (searchQuery !== "") {
      fetchHotels();
    }
  }, [searchQuery]);

  const handleSearch = (e) => {
    setSearchQuery(e.target.value);
  };

  return (
    <div>
      <h1>Hotel Search</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearch}
        placeholder="Enter location or hotel name"
      />
      <div>
        {hotels.map((hotel) => (
          <div key={hotel.id}>
            <h2>{hotel.name}</h2>
            <p>Address: {hotel.address}</p>
            {/* Add more details as needed */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
