import React, { useState } from "react";
import SearchBar from "../shared/SearchBar";
import "../styles/chatbot.css";

const Chatbot = () => {
  const [generatedContent, setGeneratedContent] = useState("");

  const handleContentUpdate = (content) => {
    console.log("Content received:", content); // Log the content received from the API
    if (content && content.parts && content.parts[0] && content.parts[0].text) {
      const text = content.parts[0].text;
      console.log("Extracted text:", text); // Log the extracted text
      setGeneratedContent(text);
    } else {
      console.log("No content found");
      setGeneratedContent("No content found");
    }
  };

  console.log("Generated content state:", generatedContent); // Log the generated content state

  return (
    <div className="chatbot">
      <h1>Gemini API Content Generator</h1>
      <SearchBar onContentUpdate={handleContentUpdate} />
      <div className="generated-content">
        <h2>Generated Content:</h2>
        <p>{generatedContent}</p>
      </div>
    </div>
  );
};

export default Chatbot;
