import React, { useState, useEffect } from "react";
import axios from "axios";

const Hotels = () => {
  const [hotels, setHotels] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  // useEffect(() => {
  //   const fetchHotels = async () => {
  //     try {
  //       const response = await axios.get(
  //         "https://booking-com15.p.rapidapi.com/api/v1/hotels/search",
  //         {
  //           params: {
  //             query: searchQuery,
  //           },
  //         }
  //       );
  //       setHotels(response.data.hotels);
  //     } catch (error) {
  //       console.error("Error fetching hotels:", error);
  //     }
  //   };

  //   if (searchQuery.trim() !== "") {
  //     fetchHotels();
  //   }
  // }, [searchQuery]);

  const fetchHotels = async () => {
    try {
      const response = await axios.get(
        "https://booking-com15.p.rapidapi.com/api/v1/hotels/search",
        {
          params: {
            query: searchQuery,
          },
          headers: {
            "Content-Type": "application/json",
            "X-RapidAPI-Host": "booking-com15.p.rapidapi.com",
            "X-RapidAPI-Key":
              "c02a254091mshc3cc59b3f13086ap1d42e2jsnbec0430ec97b",
          },
        }
      );
      setHotels(response.data.hotels);
    } catch (error) {
      console.error("Error fetching hotels:", error);
    }
  };

  const handleSearch = () => {
    if (searchQuery.trim() !== "") {
      console.log(searchQuery.trim());
      setSearchQuery(searchQuery.trim()); // Trim the search query before setting
    }
    fetchHotels();
  };

  return (
    <div>
      <h1>Hotel Search</h1>
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Enter location or hotel name"
      />
      <button onClick={handleSearch}>Search</button>
      <div className="hotels-container">
        {hotels.map((hotel) => (
          <div key={hotel.hotel_id} className="hotel-card">
            <h2>{hotel.hotel_name}</h2>
            <p>Address: {hotel.hotel_address}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Hotels;
